(function($){$.fn.square1=function(options){var _this=this;var settings;var square1_interval;var slideshow_hover=false;if(_this.length<1)return;if($(_this).data('settings')){var settings=$(_this).data('settings');square1_interval=$(_this).data('interval');}else{var settings=$.extend({width:'',height:$(_this).height(),fill_mode:'contain',background:'#fff',auto_start:true,start_delay:0,slide_duration:4000,transition_time:500,pause_on_hover:true,prev_next_nav:'inside',dots_nav:'inside',onPlay:function(){},onStop:function(){},onChange:function(){}},options);$(_this).data('settings',settings);}
if(options=='play'){run_slideshow();return;}
if(options=='stop'){settings['auto_start']=false;stop_slideshow();return;}
if(options=='next'){next_image();if(settings['auto_start'])reset_interval();return;}
if(options=='prev'){prev_image();if(settings['auto_start'])reset_interval();return;}
if(Math.floor(options)===options&&+options===options){jump_to_image(options-1);if(settings['auto_start'])reset_interval();return;}
$(_this).css({position:'relative',width:settings['width'],height:settings['height'],});_this.addClass('square1');$(_this).children().wrap('<div class="image_wrapper" />');$('.image_wrapper',_this).each(function(){var img=$(this).find('img').attr('src');$(this).css('background-image','url('+img+')');});$('.image_wrapper:first',_this).addClass('current_slide');$('.image_wrapper:not(.current_slide)',_this).hide();$(_this).append('<div class="square1_caption"></div>');$(_this).append('<div class="square1_controls"><span class="square1_prev_image">Previous Image</span><span class="square1_next_image">Next Image</span><div class="square1_dots"></div></div>');var x=0;$('.image_wrapper',_this).each(function(){$('.square1_dots',_this).append('<span data-image-num="'+x+'"></span>');x++;});$(_this).addClass('prev_next_nav-'+settings['prev_next_nav']);$(_this).addClass('dots_nav-'+settings['dots_nav']);$(_this).addClass('fill_mode-'+settings['fill_mode']);$('.square1_prev_image',_this).click(function(){prev_image();if(settings['auto_start'])reset_interval();});$('.square1_next_image',_this).click(function(){next_image();if(settings['auto_start'])reset_interval();});$('.square1_dots span',_this).click(function(){jump_to_image($(this).data('image-num'));if(settings['auto_start'])reset_interval();})
$(_this).on('mouseenter','.image_wrapper',function(){if(settings['pause_on_hover'])stop_slideshow();});$(_this).on('mouseleave','.image_wrapper',function(){if(settings['pause_on_hover']&&settings['auto_start'])run_slideshow();});if(settings['auto_start'])setTimeout(run_slideshow,settings['start_delay']);update_caption();function run_slideshow(){reset_interval();update_dots_nav();settings['onPlay']();}
function stop_slideshow(){clearInterval(square1_interval);settings['onStop']();}
function reset_interval(){clearInterval(square1_interval);square1_interval=setInterval(next_image,settings['slide_duration']+settings['transition_time']);$(_this).data('interval',square1_interval);}
function next_image(){var curr_slide_index=$('.current_slide',_this).index();if(curr_slide_index==$('.image_wrapper',_this).length-1)jump_to_image(0);else jump_to_image(curr_slide_index+1);}
function prev_image(){var curr_slide_index=$('.current_slide',_this).index();if(curr_slide_index==0)jump_to_image($('.image_wrapper',_this).last().index());else jump_to_image(curr_slide_index-1);}
function jump_to_image(image_num){if(settings['fill_mode']=='cover'){if(image_num>$('.current_slide',_this).index()){$('.current_slide',_this).removeClass('current_slide');$('.image_wrapper',_this).eq(image_num).addClass('current_slide').fadeIn(settings['transition_time'],function(){$('.image_wrapper:not(.current_slide)',_this).hide();});}
else if(image_num<$('.current_slide',_this).index()){$('.current_slide',_this).removeClass('current_slide').fadeOut(settings['transition_time']);$('.image_wrapper',_this).eq(image_num).addClass('current_slide').show();}}
else{$('.current_slide',_this).removeClass('current_slide').fadeOut(settings['transition_time']);$('.image_wrapper',_this).eq(image_num).addClass('current_slide').fadeIn(settings['transition_time']);}
update_caption();update_dots_nav();settings['onChange']();}
function update_caption(){$('.square1_caption',_this).fadeOut(settings['transition_time'],function(){setTimeout(function(){$('.square1_caption',_this).html($('.current_slide img',_this).attr('alt')).fadeIn(settings['transition_time']-200);},10)});}
function update_dots_nav(){$('.square1_dots span',_this).removeClass('current');$('.square1_dots span[data-image-num="'+$('.current_slide',_this).index()+'"]',_this).addClass('current');}
return _this;};}(jQuery));